import React, {Component} from 'react';
import "./ToDoApp.css"

import propTypes from "prop-types"
import {BrowserRouter as Router,Route,Switch,Link} from 'react-router-dom'

import AuthenticationService from './AuthenticationService.js'
import AuthenticatedRoute from "./AuthenticatedRoute.jsx"
import LoginComponent from "./LoginComponent.jsx"
import ListToDoComponent from "./ListToDoComponent"
import HelloWorldService from "../../api/todo/HelloWorldService.js"

class WelcomeComponent extends Component{
    constructor(props){
        super(props);
        this.retrieveWelcomeMessage = this.retrieveWelcomeMessage.bind(this);
        
        this.handleSuccessfulResponse = this.handleSuccessfulResponse.bind(this);
        this.state = {
            welcomeMessage:''
        }
    }
    render(){
        return (
            <div>
                <div className="container padding">
                    {/*<h2>Welcome {this.props.match.params.name} </h2>*/ /*To take value from url*/}
                    <h2>Welcome {sessionStorage.getItem('authenticatedUser')} </h2>
                    <span><Link to={"/todos/" + this.props.match.params.name }>You can manage you todo's here</Link></span>
                    
                </div>
                <div className="container padding">
                Click here to get a customized welcome message.
                <button onClick={this.retrieveWelcomeMessage}>Click here</button>
                </div>
                <div className="container padding">
                    {this.state.welcomeMessage}
                </div>
            </div>
        );
    }
    retrieveWelcomeMessage(){
        HelloWorldService.executeHelloWorldService()
        .then(response =>console.log(response))
    }

    handleSuccessfulResponse(response){
        this.setState({welcomeMessage:response.data})
    }
}

export default WelcomeComponent;
