import React, {Component} from 'react';
import "./ToDoApp.css"

import propTypes from "prop-types"
import {BrowserRouter as Router,Route,Switch,Link} from 'react-router-dom'

import AuthenticationService from './AuthenticationService.js'
import AuthenticatedRoute from "./AuthenticatedRoute.jsx"
import LoginComponent from "./LoginComponent.jsx"
import ListToDoComponent from "./ListToDoComponent"

class ToDoApp extends Component {
    constructor(){
        super();
    
    }
    render() {
        return (
            <div className="ToDoApp">
                <HeaderComponent/>
                 
                 
                <Router className >
                    <Switch> {/* To select only one route at a time */}
                        <Route path="/login" exact  component={LoginComponent}/>
                        <AuthenticatedRoute path="/welcome/:name" exact  component={WelcomeComponent}/>
                        <AuthenticatedRoute path="/todos/:name" exact  component={ListToDoComponent}/>
                        <AuthenticatedRoute path="/logout" exact  component={LogoutComponent}/>
                        <Route path="/" exact component={LoginComponent}/>
                        <Route  exact component={ErrorComponent}/>
                    </Switch>
  
                </Router>
                <FooterComponent/>
                 {/*<LoginComponent/>
                 <WelcomeComponent/>*/}
            </div>
        );
    }
    
}


class HeaderComponent extends Component{
    render(){
        const isUserLoggedIn = AuthenticationService.isUserLoggedIn();
        console.log(isUserLoggedIn);
        return (
                
                <header >
                    <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                        
                            <a className="navbar-header navbar-brand">
                                My ToDo Application
                            </a>
                           
                            <ul className="navbar-nav">
                                {isUserLoggedIn && <li> <a href="/welcome"  className="nav-link">Home</a></li>}
                                {isUserLoggedIn && <li> <a href="/todos"  className="nav-link" >ToDo's</a></li>}
                            </ul>
                            
                            <ul className="navbar-nav navbar-collapse justify-content-end">
                                {(!isUserLoggedIn) && <li><a href="/login"  className="nav-link" >Log In</a></li>}
                                {isUserLoggedIn && <li><a href="/logout"  className="nav-link" onClick={AuthenticationService.logout   }>Log Out</a></li>}
                            </ul>
                         
                    </nav>
                    
                </header>
                
                
            
        );
    }
}

class FooterComponent extends Component{
    render(){
        return (
            <div className="footer">
                <span className="text-muted">All rights reserved to Jaideep Sagar &copy;</span>
            </div>
        );
    }
}

class LogoutComponent extends Component{
    render(){
        return (
            <div className="container padding">
                <h2>You are logged out</h2>
                <div className="container">
                    Thank you for using our app. 
                </div>
            </div>
        );
    }
}

class WelcomeComponent extends Component{
    render(){
        return (
            <div className="container padding">
                {/*<h2>Welcome {this.props.match.params.name} </h2>*/ /*To take value from url*/}
                <h2>Welcome {sessionStorage.getItem('authenticatedUser')} </h2>
                <span><Link to={"/todos/" + this.props.match.params.name }>You can manage you todo's here</Link></span>
                
            </div>
        );
    }
}
function ErrorComponent(){
    return (
        <div className="container padding">
            Error Occured.
        </div>
    )
}
export default ToDoApp;
