import axios from 'axios'
export const USER_NAME_SESSION_ATTRIBUTE_NAME = 'authenticatedUser'


class AuthenticationService{

    executeBasicAuthenticationService(username,password){
        return axios.get('http://localhost:8080/restful-web-services/basicauth',
            {
                headers : {
                        authorization : this.createBasicAuthToken(username,password)
                }
            }
        
        )
    }
    executeJwtAuthenticationService(username,password){
        return axios.post('http://localhost:8080/restful-web-services/authenticate',{
            username,
            password        
        })
    }

    createBasicAuthToken(username,password){
        return 'Basic ' + window.btoa(username + ':' + password) // to encode username and password, in case of basic authentication (no form) we needto encode the credentials
    }
    createJwtToken(token){
        return 'Bearer ' + token
    }

    registerSuccessfulLogin(username,password){
        sessionStorage.setItem(USER_NAME_SESSION_ATTRIBUTE_NAME,username);
        this.setupAxiosInterceptors()
    }
    registerSuccessfulLoginForJwt(username,token){
        sessionStorage.setItem(USER_NAME_SESSION_ATTRIBUTE_NAME,username);
        this.setupAxiosInterceptorsForJwt(this.createJwtToken(token))
    }
    

    logout(){
        sessionStorage.removeItem(USER_NAME_SESSION_ATTRIBUTE_NAME);
        console.log("removed from session")
    }
    isUserLoggedIn(){
        let user = sessionStorage.getItem(USER_NAME_SESSION_ATTRIBUTE_NAME);
        if(user===null)
            return false 
          
        return true
    }
    getLoggedInUsername(){
        let user = sessionStorage.getItem(USER_NAME_SESSION_ATTRIBUTE_NAME);
        if(user===null)
            return null 
          
        return user
    }
    setupAxiosInterceptors(){
        let username = 'jaideep';
        let password = 'password';
        let basicAuthHeader = this.createBasicAuthToken(username,password) 

        axios.interceptors.request.use( // we can also intercept response
            (config) =>{
                if(this.isUserLoggedIn()){
                    config.headers.authorization = basicAuthHeader
                }
                return config
            }
        )
    }
    setupAxiosInterceptorsForJwt(token){
       
       

        axios.interceptors.request.use( // we can also intercept response
            (config) =>{
                if(this.isUserLoggedIn()){
                    config.headers.authorization = token
                }
                return config
            }
        )
    }
}

export default new AuthenticationService()
