import React, {Component} from 'react';
import "./Counter.css"
class Counter extends Component{
    constructor(){
        super();
        this.state = {
            counter : 0,
            secondCounter : 100
        }

        this.increment  = this.increment.bind(this); // we are binding this with increment
    }
  
    render(){
        return (
        <div className="Counter">
            <h1>Counter</h1>
            <button className="number-button" onClick={this.increment}>+1</button>
            <span className="result">{this.state.counter}</span>
            <span className="result">{this.state.secondCounter}</span>
        </div>
        );
    }

    increment(){
        //this.state.counter ++;
        this.setState({
            counter : this.state.counter + 1
        })
    }
}



export default Counter;
