import React, {Component} from 'react';
import "./ToDoApp.css"
import propTypes from "prop-types"
import {BrowserRouter as Router,Route,Switch} from 'react-router-dom'


class ToDoApp extends Component {
    constructor(){
        super();
    
    }
    render() {
        return (
            <div className="ToDoApp">
                 <h1>My ToDo Application </h1>
                <Router>
                    <Switch> {/* To select only one route at a time */}
                        <Route path="/login" exact  component={LoginComponent}/>
                        <Route path="/welcome/:name" exact  component={WelcomeComponent}/>
                        <Route path="/todos/:name" exact  component={ListToDoComponent}/>
                        <Route path="/" exact component={LoginComponent}/>
                        <Route  exact component={ErrorComponent}/>
                    </Switch>
  
                </Router>
                 {/*<LoginComponent/>
                 <WelcomeComponent/>*/}
            </div>
        );
    }
    
}

class LoginComponent extends Component{
    constructor(props){
        super(props);
        this.state = {
            username : "jaideep",
            password : "",
            hasLoginFailed : false,
            showSuccessMessage : false
        }
        this.handleUsernameChange = this.handleUsernameChange.bind(this)
        this.handlePasswordChange = this.handlePasswordChange.bind(this)
        this.handleAnyChange = this.handleAnyChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
        //this.showInvalidCredentials = this.showInvalidCredentials.bind(this)
    }
    handleAnyChange(event){
        console.log(this.state)
        this.setState({
            [event.target.name] : event.target.value // [] to access object related to event.target.name which is username in case of change in username and etc
        });
    }
    handleUsernameChange(event){
        console.log(event.target.value)
        this.setState({
            username : event.target.value
        });
    }
    handlePasswordChange(event){
        console.log(event.target.value)
        this.setState({
            password : event.target.value
        });
    }
    loginClicked(event){
        
        if(this.state.username === "jaideep" && this.state.password==="pass"){
            this.props.history.push(`/welcome/${this.state.username}`) // To route to /welcome url
            this.setState({showSuccessMessage:true})
            this.setState({hasLoginFailed:false})
        }
        else{
            
            this.setState({showSuccessMessage:false})
            this.setState({hasLoginFailed:true})
        }
    }
    
    render() {
        return (
            <div>
                <table>
                    <tr>
                        <td>User Name : </td><td><input type="text" name="username" value={this.state.username} onChange = {this.handleAnyChange}/></td>
                    </tr>
                    <tr>
                        <td>Password : </td><td> <input type="password" name="password" value={this.state.password} onChange = {this.handleAnyChange}/></td>
                    </tr>
                    <tr>
                        <td><button onClick = {this.loginClicked}>Log In</button></td>
                    </tr>
                    <tr>
                        <td>
                            <ShowMessage hasLoginFailed={this.state.hasLoginFailed} showSuccessMessage={this.state.showSuccessMessage}/>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            {this.state.hasLoginFailed && !this.state.showSuccessMessage&& <div >2 Incorrect credentials!!</div>}
                            {this.state.showSuccessMessage &&  <div >2 Correct credentials!!</div>}
                        </td>
                    </tr>
                </table>

                
            </div>
        );
    }
}
function ShowMessage(props){
        if(props.hasLoginFailed){
            return <div >Incorrect credentials!!</div>
        }
        else if(props.showSuccessMessage){
            return <div>Correct credentials!!</div>
        }
        else 
            return null;
}
class ListToDoComponent extends Component{
    constructor(props){
        super(props);
        this.state = {
            todos :
            [
                {id:1,description :"Learn React"},
                {id:2,description :"Make money"},
                {id:3,description :"write story"}
            ]
        }
    }
    render(){
        return (
            <div>
                <h2>Welcome {this.props.match.params.name} </h2>
                <h3>List To Do</h3>
                <table border='1'>
                    <thead>
                        <tr>
                            <th> Id </th> <th> Description </th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map(
                                todo =>
                                <tr>
                                    <td>{todo.id}</td> <td>{todo.description}</td>
                                </tr>
                            ) 
                        }                      
                    </tbody>
                </table>
            </div>
        );
    }
}
class WelcomeComponent extends Component{
    render(){
        return (
            <div>
                <h1>Welcome {this.props.match.params.name} </h1>
                <span><Link to="/todos">You can manage you todo's here</Link></span>
                
            </div>
        );
    }
}
function ErrorComponent(){
    return (
        <div>
            Error Occured.
        </div>
    )
}
export default ToDoApp;
