import axios from 'axios'

class TodoDataService{
    retrieveAllTodos(name){
        return axios.get(`http://localhost:8080/restful-web-services/users/${name}/todos`); // we are returning the Promise back, and WelcomeComponent will decide what to do if it is successfull / unsuccessfull
       // console.log("Executed")
    }
    retrieveTodo(name,id){
        return axios.get(`http://localhost:8080/restful-web-services/users/${name}/todos/${id}`); // we are returning the Promise back, and WelcomeComponent will decide what to do if it is successfull / unsuccessfull
       // console.log("Executed")
    }
    deleteTodo(name,id){
        return axios.delete(`http://localhost:8080/restful-web-services/users/${name}/todos/${id}`); // we are returning the Promise back, and WelcomeComponent will decide what to do if it is successfull / unsuccessfull
       // console.log("Executed")
    }
   
}

export default new TodoDataService();
