import React, {Component} from 'react';
import "./ToDoApp.css"

import propTypes from "prop-types"
import {BrowserRouter as Router,Route,Switch,Link} from 'react-router-dom'
import AuthenticationService from './AuthenticationService.js'
import AuthenticatedRoute from "./AuthenticatedRoute.jsx"
import TodoDataService from "../../api/todo/TodoDataService.js"


class ListToDoComponent extends Component{  // Initial API call should not be done in constructor, the state will not be initialized untill the API call is completed, which s nto a good state to be in
    constructor(props){
        super(props);
        this.state = {
            todos :
            [
                // {id:1,description :"Learn React", done:false, targetDate : new Date},
                // {id:2,description :"Make money", done:false, targetDate : new Date},
                // {id:3,description :"write story", done:false, targetDate : new Date}
            ]
        }
    }
    componentDidMount(){ // when component is mounted it is called 
        let username = AuthenticationService.getLoggedInUsername();
        TodoDataService.retrieveAllTodos(username);
    }
    
    render(){
        return (
            <div className="container padding">
                {/*<h2>Welcome {this.props.match.params.name} </h2>*/}
                <h2>Welcome {sessionStorage.getItem('authenticatedUser')} </h2>
                <h3 className='header-todo'>List To Do</h3>
                <div className="container ">
                    <div> 
                        <button className="btn btn-success action-button">Auto Priortize </button>
                        <button className="btn btn-success action-button">Add Task</button>  
                        <button className="btn btn-success action-button">Remove Tasks</button>  
                    </div>
                    <table className="table">
                        <thead >
                            <tr>
                                    <th>Description</th> <th>Target Date</th> <th>Completed?</th>
                            </tr>
                        </thead>
                        <tbody>

                            {
                                this.state.todos.map(
                                    todo =>
                                    <tr key={todo.id}>
                                        <td>{todo.description}</td><td>{todo.targetDate.toString()}</td> <td>{todo.done.toString()}</td>
                                    </tr>
                                ) 
                            }                      
                        </tbody>
                    </table>
                </div>
                
            </div>
        );
    }
}


export default ListToDoComponent;
