       // console.log("Executed")
    }
    createTodo(name,todo){
        return axios.post(`${API_URL}/users/${name}/todos`,todo); // we are returning the Promise back, and WelcomeComponent will decide what to do if it is successfull / unsuccessfull
       // console.log("Executed")
    }
   
}

export default new TodoDataService();
