import React, {Component} from 'react';
import "./ToDoApp.css"
import moment from 'moment'
import {Formik,Form, Field,ErrorMessage} from 'formik'

class TodoComponent extends Component{
    constructor(props){
        super(props);
        this.state = {
            id :this.props.match.params.id,
            description : "learn react",
            targetDate : moment(new Date()).format("YYYY-MM-DD")
        }
        this.onSubmit = this.onSubmit.bind(this);
        this.validate = this.validate.bind(this);
    }
    onSubmit(values){
        console.log(values)
    }
    validate(values){
        console.log(values);
        let errors = {description:"Description should have atleast 5 characters"}
        return errors;
    }
    render(){
        let description = this.state.description;
        let targetDate = this.state.targetDate;
        return (
            <div>
                <h1> Todo </h1>
                <div className="container">

                    <Formik initialValues = {{description, targetDate }} onSubmit={this.onSubmit} validate={this.validate}>
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="description" component="div" className="alert alert-warning"/>
                                    <fieldset className="form-group">
                                        <label>Description</label>
                                        <Field className="form-control" type ="text" name="description"/>
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Target Date</label>
                                        <Field className="form-control" type ="date" name="targetDate"/>
                                    </fieldset>
                                    <button type="submit" className="btn btn-success padding">Save</button>
                                </Form>
                            )
                        }

                    </Formik>
                </div>

            </div>
        );
    }
}

export default TodoComponent;
